package com.example.android.wearable.composestarter.componentsimport android.graphics.drawable.Iconimport androidx.compose.foundation.layout.Arrangementimport androidx.compose.foundation.layout.Rowimport androidx.compose.foundation.layout.fillMaxWidthimport androidx.compose.foundation.layout.widthimport androidx.compose.material.icons.Iconsimport androidx.compose.material.icons.filled.ArrowBackimport androidx.compose.material.icons.filled.Businessimport androidx.compose.material.icons.filled.FlipToFrontimport androidx.compose.material.icons.filled.Visibilityimport androidx.compose.runtime.Composableimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.graphics.vector.ImageVectorimport androidx.navigation.NavControllerimport androidx.wear.compose.material.*import com.example.android.wearable.composestarter.presentation.theme.wearColorPalette@Composablefun OptionMenu(    modifier: Modifier,    text: String,    icon: ImageVector,    navController: NavController,    goToScreen: String) {    Row(        modifier = modifier.fillMaxWidth(0.6f),        horizontalArrangement = Arrangement.Center,        verticalAlignment = Alignment.CenterVertically    ) {        Text(            text = text, color = wearColorPalette.onSurfaceVariant        )        CompactButton(            colors = ButtonDefaults.buttonColors(                contentColor = wearColorPalette.onSurfaceVariant,                backgroundColor = wearColorPalette.primary            ), onClick = {                navController.navigate(goToScreen)            }) {            Icon(imageVector = icon, contentDescription = text)        }    }}