package com.example.android.wearable.composestarter.componentsimport android.graphics.drawable.Iconimport androidx.compose.foundation.layout.*import androidx.compose.material.icons.Iconsimport androidx.compose.material.icons.filled.*import androidx.compose.runtime.Composableimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.graphics.vector.ImageVectorimport androidx.compose.ui.modifier.modifierLocalConsumerimport androidx.compose.ui.unit.dpimport androidx.navigation.NavControllerimport androidx.wear.compose.material.*import com.example.android.wearable.composestarter.presentation.theme.wearColorPalette@Composablefun OptionMenu(    modifier: Modifier,    text: String,    icon: ImageVector,    navController: NavController,    goToScreen: String) {    Row(        modifier = modifier.fillMaxWidth(0.6f),        horizontalArrangement = Arrangement.Center,        verticalAlignment = Alignment.CenterVertically    ) {        Text(            text = text, color = wearColorPalette.onSurfaceVariant        )        CompactButton(            colors = ButtonDefaults.buttonColors(                contentColor = wearColorPalette.onSurfaceVariant,                backgroundColor = wearColorPalette.primary            ), onClick = {                navController.navigate(goToScreen)            }) {            Icon(imageVector = icon, contentDescription = text)        }    }}@Composablefun ReturnHome(navController: NavController) {    Row(        modifier = Modifier.fillMaxWidth().padding(0.dp),        verticalAlignment = Alignment.CenterVertically,        horizontalArrangement = Arrangement.Center    ) {        CompactButton(            modifier = Modifier                .fillMaxWidth(),            onClick = { navController.popBackStack() },            colors = ButtonDefaults.buttonColors(                contentColor = wearColorPalette.primary,                backgroundColor = Color.Transparent            )        ) {            Icon(imageVector = Icons.Filled.Home, contentDescription = "Go back to Home")        }    }}