package com.example.android.wearable.composestarter.navigationimport androidx.compose.runtime.Composableimport androidx.navigation.compose.NavHostimport androidx.navigation.compose.composableimport androidx.navigation.compose.rememberNavControllerimport com.example.android.wearable.composestarter.presentation.screens.*@Composablefun WearNavigation() {    val navController = rememberNavController()    NavHost(navController = navController, startDestination = WearScreens.SplashScreen.name) {        /** Splash Screen */        composable(WearScreens.SplashScreen.name) {            SplashScreen(navController = navController)        }        /** Home Screen */        composable(WearScreens.ButtonsScreen.name) {            ButtonsScreen(navController = navController)        }        /** Home Screen */        composable(WearScreens.TextsScreen.name) {            TextsScreen(navController = navController)        }        /** Home Screen */        composable(WearScreens.WeatherScreen.name) {            WeatherScreen(navController = navController)        }        /** Home Screen */        composable(WearScreens.ChipAndScalingLC.name) {            ChipAndScalingLazyColumn(navController = navController)        }        /** Home Screen */        composable(WearScreens.HomeScreen.name) {            HomeScreen(navController = navController)        }    }}