package com.example.android.wearable.composestarter.navigationimport androidx.compose.runtime.Composableimport androidx.navigation.compose.NavHostimport androidx.navigation.compose.composableimport androidx.navigation.compose.rememberNavControllerimport com.example.android.wearable.composestarter.presentation.screens.HomeScreenimport com.example.android.wearable.composestarter.presentation.screens.SplashScreen@Composablefun WearNavigation() {    val navController = rememberNavController()    NavHost(navController = navController, startDestination = WearScreens.SplashScreen.name) {        /** Splash Screen */        composable(WearScreens.SplashScreen.name) {            SplashScreen(navController = navController)        }        /** Home Screen */        composable(WearScreens.HomeScreen.name) {            HomeScreen(navController = navController)        }    }}