package com.example.android.wearable.composestarter.navigationenum class WearScreens {    SplashScreen,    HomeScreen,    ButtonsScreen,    TextsScreen,    WeatherScreen;    companion object {        fun fromRoute(route: String?): WearScreens = when (route?.substringBefore("/")) {            SplashScreen.name -> SplashScreen            HomeScreen.name -> HomeScreen            ButtonsScreen.name -> ButtonsScreen            TextsScreen.name -> TextsScreen            else -> WeatherScreen        }    }}