package com.example.android.wearable.composestarter.navigationenum class WearScreens {    SplashScreen,    ButtonsScreen,    TextsScreen,    WeatherScreen,    HomeScreen;    companion object {        fun fromRoute(route: String?): WearScreens = when (route?.substringBefore("/")) {            SplashScreen.name -> SplashScreen            ButtonsScreen.name -> ButtonsScreen            TextsScreen.name -> TextsScreen            WeatherScreen.name -> WeatherScreen            else -> HomeScreen        }    }}