package com.example.android.wearable.composestarter.navigationenum class WearScreens {    SplashScreen,    ButtonsScreen,    TextsScreen,    WeatherScreen,    ChipAndScalingLCScreen,    CardsScreen,    HomeScreen;    companion object {        fun fromRoute(route: String?): WearScreens = when (route?.substringBefore("/")) {            SplashScreen.name -> SplashScreen            ButtonsScreen.name -> ButtonsScreen            TextsScreen.name -> TextsScreen            WeatherScreen.name -> WeatherScreen            ChipAndScalingLCScreen.name -> ChipAndScalingLCScreen            CardsScreen.name -> CardsScreen            else -> HomeScreen        }    }}