package com.example.android.wearable.composestarter.presentation.screensimport androidx.compose.foundation.backgroundimport androidx.compose.foundation.layout.Arrangementimport androidx.compose.foundation.layout.fillMaxWidthimport androidx.compose.foundation.layout.paddingimport androidx.compose.foundation.shape.RoundedCornerShapeimport androidx.compose.runtime.Composableimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.text.TextStyleimport androidx.compose.ui.text.style.TextAlignimport androidx.compose.ui.unit.dpimport androidx.navigation.NavControllerimport androidx.wear.compose.material.ScalingLazyColumnimport androidx.wear.compose.material.ScalingLazyListStateimport androidx.wear.compose.material.Textimport androidx.wear.compose.material.itemsimport com.example.android.wearable.composestarter.presentation.theme.wearColorPaletteimport com.example.android.wearable.composestarter.utils.LOREM_IPSUM@Composablefun ChipAndScalingLazyColumn(navController: NavController) {    ScalingLazyColumn(        modifier = Modifier            .fillMaxWidth()            .padding(bottom = 36.dp),        state = ScalingLazyListState(),        verticalArrangement = Arrangement.Center,        horizontalAlignment = Alignment.CenterHorizontally    ) {        val quotes = LOREM_IPSUM.split(".").count() - 1        items(items = LOREM_IPSUM.split(".").subList(0, quotes)) { quote ->            Text(                modifier = Modifier                    .fillMaxWidth(0.9f)                    .padding(8.dp)                    .background(                        color = wearColorPalette.onBackground.copy(alpha = 0.2f),                        shape = RoundedCornerShape(12.dp)                    ),                style = TextStyle(color = wearColorPalette.onBackground),                text = "$quote.",                textAlign = TextAlign.Center            )        }    }}