package com.example.android.wearable.composestarter.presentation.screensimport androidx.compose.foundation.layout.*import androidx.compose.material.icons.Iconsimport androidx.compose.material.icons.filled.Cloudimport androidx.compose.material.icons.filled.TextFieldsimport androidx.compose.material.icons.filled.Translateimport androidx.compose.material.icons.filled.Visibilityimport androidx.compose.runtime.*import androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.unit.dpimport androidx.navigation.NavControllerimport com.example.android.wearable.composestarter.components.OptionMenuimport com.example.android.wearable.composestarter.navigation.WearScreens@Composablefun HomeScreen(navController: NavController) {    val myModifier = Modifier        .fillMaxWidth()        .padding(8.dp)    Column(        modifier = myModifier.fillMaxHeight(),        verticalArrangement = Arrangement.Center,        horizontalAlignment = Alignment.CenterHorizontally    ) {        OptionMenu(myModifier, "Example buttons", icon = Icons.Filled.Visibility, navController, WearScreens.ButtonsScreen.name)        OptionMenu(myModifier, "Example texts", icon = Icons.Filled.TextFields, navController, WearScreens.TextsScreen.name)        OptionMenu(myModifier, "Example weather", icon = Icons.Filled.Cloud, navController, WearScreens.WeatherScreen.name)    }}