package com.example.android.wearable.composestarter.presentation.screensimport androidx.compose.foundation.ScrollStateimport androidx.compose.foundation.gestures.rememberScrollableStateimport androidx.compose.foundation.gestures.scrollableimport androidx.compose.foundation.layout.*import androidx.compose.foundation.rememberScrollStateimport androidx.compose.foundation.shape.CircleShapeimport androidx.compose.foundation.verticalScrollimport androidx.compose.material.icons.Iconsimport androidx.compose.material.icons.filled.Cloudimport androidx.compose.material.icons.filled.CreditCardimport androidx.compose.material.icons.filled.TextFieldsimport androidx.compose.material.icons.filled.Visibilityimport androidx.compose.runtime.*import androidx.compose.runtime.saveable.rememberSaveableimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.draw.clipimport androidx.compose.ui.unit.dpimport androidx.navigation.NavControllerimport com.example.android.wearable.composestarter.components.OptionMenuimport com.example.android.wearable.composestarter.navigation.WearScreens@Composablefun HomeScreen(navController: NavController) {    val myModifier = Modifier        .fillMaxWidth()        .padding(8.dp)    Column(        modifier = myModifier            .padding(0.dp)            .fillMaxHeight()            .verticalScroll(ScrollState(0), true),        verticalArrangement = Arrangement.Center,        horizontalAlignment = Alignment.CenterHorizontally    ) {        OptionMenu(myModifier, "Example buttons", icon = Icons.Filled.Visibility, navController, WearScreens.ButtonsScreen.name)        OptionMenu(myModifier, "Example texts", icon = Icons.Filled.TextFields, navController, WearScreens.TextsScreen.name)        OptionMenu(myModifier, "Example weather", icon = Icons.Filled.Cloud, navController, WearScreens.WeatherScreen.name)        OptionMenu(myModifier, "Example cards", icon = Icons.Filled.CreditCard, navController, WearScreens.WeatherScreen.name)    }}