package com.example.android.wearable.composestarter.presentation.screensimport android.icu.number.Scaleimport android.view.animation.OvershootInterpolatorimport androidx.compose.animation.core.Animatableimport androidx.compose.animation.core.tweenimport androidx.compose.foundation.layout.*import androidx.compose.runtime.Composableimport androidx.compose.runtime.LaunchedEffectimport androidx.compose.runtime.rememberimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.draw.scaleimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.res.stringResourceimport androidx.compose.ui.text.TextStyleimport androidx.compose.ui.text.font.FontStyleimport androidx.compose.ui.text.font.FontWeightimport androidx.compose.ui.text.style.TextAlignimport androidx.compose.ui.unit.dpimport androidx.compose.ui.unit.spimport androidx.navigation.NavControllerimport androidx.wear.compose.material.MaterialThemeimport androidx.wear.compose.material.Scaffoldimport androidx.wear.compose.material.Textimport com.example.android.wearable.composestarter.Rimport com.example.android.wearable.composestarter.navigation.WearScreensimport com.example.android.wearable.composestarter.presentation.theme.wearColorPaletteimport kotlinx.coroutines.delay@Composablefun SplashScreen(navController: NavController) {    val scale = remember {        Animatable(0f)    }    LaunchedEffect(        key1 = true,        block = {            scale.animateTo(                targetValue = 0.9f,                animationSpec = tween(                    durationMillis = 800,                    easing = {                        OvershootInterpolator(8f).getInterpolation(it)                    }                )            )            delay(2000L)            navController.navigate(WearScreens.HomeScreen.name)        })    Scaffold() {        Column(            modifier = Modifier                .fillMaxSize()                .scale(scale.value),            verticalArrangement = Arrangement.Center,            horizontalAlignment = Alignment.CenterHorizontally        ) {            Text(                modifier = Modifier.fillMaxWidth(),                textAlign = TextAlign.Center,                color = wearColorPalette.primary,                text = stringResource(R.string.hello_world),                style = TextStyle(                    fontSize = 24.sp,                    fontStyle = FontStyle.Italic,                    fontWeight = FontWeight.Bold                )            )        }    }}