package com.example.android.wearable.composestarter.presentation.screensimport androidx.compose.foundation.backgroundimport androidx.compose.foundation.layout.*import androidx.compose.foundation.lazy.LazyColumnimport androidx.compose.foundation.lazy.LazyListStateimport androidx.compose.foundation.lazy.itemsimport androidx.compose.foundation.shape.RoundedCornerShapeimport androidx.compose.runtime.Composableimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.text.TextStyleimport androidx.compose.ui.text.font.FontWeightimport androidx.compose.ui.text.style.TextAlignimport androidx.compose.ui.unit.dpimport androidx.compose.ui.unit.spimport androidx.navigation.NavControllerimport androidx.wear.compose.material.Textimport com.example.android.wearable.composestarter.components.ReturnHomeimport com.example.android.wearable.composestarter.presentation.theme.wearColorPaletteimport com.example.android.wearable.composestarter.utils.Constants.LOREM_IPSUM@Composablefun TextsScreen(navController: NavController) {    Column(modifier = Modifier.fillMaxSize()) {        ReturnHome(navController = navController)        Text(            modifier = Modifier                .fillMaxWidth(),            style = TextStyle(                textAlign = TextAlign.Center,                fontSize = 16.sp,                fontWeight = FontWeight.Bold,                color = Color.Green.copy(alpha = 0.8f)            ),            text = "Example Text"        )        LazyColumn(            modifier = Modifier                .fillMaxWidth()                .padding(bottom = 36.dp),            state = LazyListState(),            verticalArrangement = Arrangement.Center,            horizontalAlignment = Alignment.CenterHorizontally        ) {            val quotes = LOREM_IPSUM.split(".").count() - 1            items(items = LOREM_IPSUM.split(".").subList(0, quotes)) { quote ->                Text(                    modifier = Modifier                        .fillMaxWidth(0.9f)                        .padding(8.dp)                        .background(                            color = wearColorPalette.onBackground.copy(alpha = 0.2f),                            shape = RoundedCornerShape(12.dp)                        ),                    style = TextStyle(color = wearColorPalette.onBackground),                    text = "$quote.",                    textAlign = TextAlign.Center                )            }        }    }}