package com.example.android.wearable.composestarter.presentation.screensimport androidx.compose.foundation.Imageimport androidx.compose.foundation.layout.*import androidx.compose.runtime.Composableimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.graphics.ColorFilterimport androidx.compose.ui.res.painterResourceimport androidx.compose.ui.text.TextStyleimport androidx.compose.ui.unit.dpimport androidx.compose.ui.unit.spimport androidx.navigation.NavControllerimport androidx.wear.compose.material.Textimport com.example.android.wearable.composestarter.Rimport com.example.android.wearable.composestarter.components.ReturnHomeimport com.example.android.wearable.composestarter.presentation.theme.wearColorPalette@Composablefun WeatherScreen(navController: NavController) {    Column(        modifier = Modifier.fillMaxSize(),        verticalArrangement = Arrangement.SpaceEvenly,        horizontalAlignment = Alignment.CenterHorizontally    ) {        ReturnHome(navController = navController)        Text(text = "Fri, Jul 10")        Row(            modifier = Modifier.fillMaxWidth(),            verticalAlignment = Alignment.CenterVertically,            horizontalArrangement = Arrangement.SpaceEvenly        ) {            Image(                painter = painterResource(id = R.drawable.sun),                contentDescription = "The Sun",                colorFilter = ColorFilter.lighting(Color.White, wearColorPalette.primaryVariant),                modifier = Modifier.size(60.dp)            )            Text(                text = "29ยบ",                style = TextStyle(fontSize = 30.sp, color = wearColorPalette.primaryVariant)            )        }        Row(            modifier = Modifier.fillMaxWidth().padding(bottom = 24.dp),            verticalAlignment = Alignment.CenterVertically,            horizontalArrangement = Arrangement.Center        ) {            Row(                modifier = Modifier.padding(8.dp),                verticalAlignment = Alignment.CenterVertically,                horizontalArrangement = Arrangement.Center) {                Image(                    painter = painterResource(id = R.drawable.humidity),                    contentDescription = "Humidity Icon",                    colorFilter = ColorFilter.lighting(Color.White, wearColorPalette.primaryVariant),                    modifier = Modifier.size(10.dp)                )                Text(text = "60%")            }            Text(text = "Clouds")            Row(                modifier = Modifier.padding(8.dp),                verticalAlignment = Alignment.CenterVertically,                horizontalArrangement = Arrangement.Center) {                Image(                    painter = painterResource(id = R.drawable.wind),                    contentDescription = "Humidity Icon",                    colorFilter = ColorFilter.lighting(Color.White, wearColorPalette.primaryVariant),                    modifier = Modifier.size(10.dp)                )                Text(text = "6 kmh")            }        }    }}